import React, { useState, useEffect } from "react";
import "../styles/stylez.css";

const EmployerDash = () => {
  const [stats, setStats] = useState({
    active: 0,
    pending: 0,
    totalApplications: 0,
  });
  const [applicants, setApplicants] = useState([]);
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/employerStats/employer/stats", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setStats(await res.json());
      } catch (err) {
        console.error("Error fetching dashboard stats", err);
      }
    };

    const fetchApplicants = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/employer/applicants", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setApplicants(await res.json());
      } catch (err) {
        console.error("Error fetching applicants:", err);
      }
    };

    fetchStats();
    fetchApplicants();
  }, [token]);

  const handleStatusUpdate = async (applicationId, status) => {
    // unchanged from yours...
  };

  // ðŸ“Œ NEW: Rate applicant after acceptance
  const rateApplicant = async (applicationId, rating) => {
    try {
      const res = await fetch("http://localhost:5000/api/employer/rate-applicant", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ applicationId, rating }),
      });
      if (!res.ok) throw new Error("Rating failed");
      const data = await res.json();
      console.log("Applicant rated:", data);
      setApplicants((prev) =>
        prev.map((app) =>
          app.applicationId === applicationId
            ? { ...app, rating: data.rating, hasRated: true }
            : app
        )
      );
    } catch (err) {
      console.error("Failed to rate:", err);
      alert("Failed to rate applicant");
    }
  };

  return (
    <div id="webcrumbs">
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
        <main className="container mx-auto px-4 py-8">
          {/* Keep your stats section */}
          {/* ... */}

          {/* Applicants List */}
          <section className="mb-10">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">My Applicants</h2>
            <div className="bg-white rounded-lg shadow-sm border border-gray-100 overflow-hidden">
              {applicants.length === 0 ? (
                <p className="p-4 text-gray-500">No one has applied yet.</p>
              ) : (
                <ul className="divide-y divide-gray-100">
                  {applicants.map((app) => (
                    <li key={app.applicationId} className="p-4 hover:bg-gray-50">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center">
                          {/* Avatar, name, job title */}
                          {/* ... */}
                        </div>

                        <div className="flex gap-2 items-center">
                          {/* Application Status */}
                          <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                            app.status === "pending"
                              ? "bg-yellow-100 text-yellow-700"
                              : app.status === "accepted"
                              ? "bg-green-100 text-green-700"
                              : "bg-red-100 text-red-700"
                          }`}>
                            {app.status}
                          </span>

                          {/* Approve/Reject Buttons */}
                          {app.status === "pending" && (
                            <>
                              <button onClick={() => handleStatusUpdate(app.applicationId, "accepted")}
                                className="text-green-600 hover:underline text-sm">
                                Approve
                              </button>
                              <button onClick={() => handleStatusUpdate(app.applicationId, "rejected")}
                                className="text-red-600 hover:underline text-sm">
                                Reject
                              </button>
                            </>
                          )}

                          {/* NEW: Rating Controls */}
                          {app.status === "accepted" && (
                            <div className="flex items-center space-x-2">
                              {app.hasRated ? (
                                <span className="text-sm text-green-600">Rated: {app.rating}/5</span>
                              ) : (
                                <>
                                  <label className="text-sm">Rate:</label>
                                  {[1,2,3,4,5].map((n) => (
                                    <button
                                      key={n}
                                      onClick={() => rateApplicant(app.applicationId, n)}
                                      className="px-2 py-1 bg-gray-200 rounded hover:bg-primary-200"
                                    >
                                      {n}
                                    </button>
                                  ))}
                                </>
                              )}
                            </div>
                          )}
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default EmployerDash;
