import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/style.css";

const Jobs = () => {
  const [Jobs, setJobs] = useState([]);
  const [role, setRole] = useState("");

  useEffect(() => {
    setRole(localStorage.getItem("role"));
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/jobs");
      setJobs(res.data);
    } catch (err) {
      console.error("Error fetching jobs:", err);
    }
  };

  const applyToJob = async (jobId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:5000/api/applications",
        { jobId },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      );

      alert("Applied successfully!");
    } catch (err) {
      console.error("Failed to apply:", err);
      alert("Failed to apply");
    }
  };

  return (
    <div id="webcrumbs">
      <div className="w-full min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-6">
        <div className="max-w-7xl mx-auto">
          <header className="mb-8">
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
              <div>
                <h1 className="text-4xl font-bold text-slate-800 mb-2">
                  Browse Informal Job Listings
                </h1>
                <p className="text-slate-600">
                  View opportunities posted by local employers for casual and
                  short-term work
                </p>
              </div>
            </div>
          </header>

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
            <aside className="lg:col-span-1">
              <div className="bg-white rounded-2xl shadow-sm border border-slate-200 p-6 sticky top-6">
                <h3 className="text-lg font-semibold text-slate-800 mb-4">
                  Filter Listings
                </h3>
                {/* Filters UI goes here */}
              </div>
            </aside>

            <main className="lg:col-span-3">
              <div className="grid gap-6">
                {Jobs.length === 0 ? (
                  <p className="text-slate-500 text-center">No jobs found.</p>
                ) : (
                  Jobs.map((job) => (
                    <div
                      key={job.jobId}
                      className="bg-white border border-slate-200 rounded-xl p-6 shadow-sm hover:shadow-md transition"
                    >
                      <div className="flex flex-col sm:flex-row sm:justify-between sm:items-center gap-4">
                        <div>
                          <h2 className="text-xl font-semibold text-slate-800 mb-1">
                            {job.title}
                          </h2>
                          <p className="text-slate-600 text-sm mb-1">
                            {job.location}
                          </p>
                          <p className="text-slate-700">{job.description}</p>
                          <p className="text-indigo-600 text-sm mt-2">
                            {job.category}
                          </p>
                        </div>

                        {role === "applicant" && (
                          <div className="flex flex-col items-end gap-2">
                            <button
                              className="mt-4 px-4 py-2 bg-indigo-600 text-black rounded hover:bg-indigo-700"
                              onClick={() => applyToJob(job.jobId)}
                            >
                              Apply
                            </button>

                            <button
                              className="text-sm text-slate-500 hover:text-slate-700"
                              onClick={() => alert(`Bookmarking job ID ${job.jobId}`)}
                            >
                              Bookmark
                            </button>
                          </div>
                        )}
                      </div>
                    </div>
                  ))
                )}
              </div>
            </main>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Jobs;
