import React, { useState, useEffect } from "react";
import axios from "axios";
import "../styles/style.css";

const Jobs = () => {
  const [Jobs, setJobs] = useState([]);
  const [searchKeyword, setSearchKeyword] = useState("");
  const [selectedTypes, setSelectedTypes] = useState([]);
  const [locationFilter, setLocationFilter] = useState("");
  const [payRange, setPayRange] = useState([]);
  const [sortBy, setSortBy] = useState("Most Recent");

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/jobs");
      setJobs(res.data);
    } catch (err) {
      console.error("Error fetching jobs:", err);
    }
  };

  const applyToJob = async (jobId) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post("http://localhost:5000/api/applications", { jobId }, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Applied successfully!");
    } catch (err) {
      console.error("Failed to apply:", err);
      alert("Failed to apply");
    }
  };

  const handleTypeChange = (type) => {
    setSelectedTypes((prev) =>
      prev.includes(type) ? prev.filter((t) => t !== type) : [...prev, type]
    );
  };

  const handlePayRangeChange = (range) => {
    setPayRange((prev) =>
      prev.includes(range) ? prev.filter((r) => r !== range) : [...prev, range]
    );
  };

  const filteredJobs = Jobs.filter((job) =>
    job.title.toLowerCase().includes(searchKeyword.toLowerCase()) ||
    job.description.toLowerCase().includes(searchKeyword.toLowerCase())
  )
    .filter((job) =>
      selectedTypes.length === 0 || selectedTypes.includes(job.type)
    )
    .filter((job) =>
      locationFilter === "" || job.location.toLowerCase().includes(locationFilter.toLowerCase())
    )
    .filter((job) => {
      if (payRange.length === 0) return true;
      if (payRange.includes("Below 500")) return job.pay < 500;
      if (payRange.includes("500 - 1000")) return job.pay >= 500 && job.pay <= 1000;
      if (payRange.includes("Above 1000")) return job.pay > 1000;
      return true;
    })
    .sort((a, b) => {
      if (sortBy === "Most Recent") return new Date(b.createdAt) - new Date(a.createdAt);
      if (sortBy === "Highest Pay") return b.pay - a.pay;
      return 0;
    });

  return (
    <div id="webcrumbs">
      <div className="w-full min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 p-6">
        <div className="max-w-7xl mx-auto">
          <header className="mb-8">
            <div className="flex flex-col md:flex-row md:items-center md:justify-between gap-4">
              <div>
                <h1 className="text-4xl font-bold text-slate-800 mb-2">Browse Informal Job Listings</h1>
                <p className="text-slate-600">View opportunities posted by local employers for casual and short-term work</p>
              </div>
              <div className="flex flex-col sm:flex-row gap-3">
                <div className="relative">
                  <input
                    type="text"
                    placeholder="e.g. Mason, Tailor, Driver..."
                    value={searchKeyword}
                    onChange={(e) => setSearchKeyword(e.target.value)}
                    className="w-full sm:w-80 pl-10 pr-4 py-3 rounded-full border border-slate-300"
                  />
                  <span className="material-symbols-outlined absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400">
                    search
                  </span>
                </div>
              </div>
            </div>
          </header>

          <div className="grid grid-cols-1 lg:grid-cols-4 gap-6">
            <aside className="lg:col-span-1">
              <div className="bg-white rounded-2xl shadow-sm border p-6 sticky top-6">
                <h3 className="text-lg font-semibold text-slate-800 mb-4">Filter Listings</h3>
                <div className="space-y-6">
                  <div>
                    <h4 className="font-medium text-slate-700 mb-3">Job Type</h4>
                    {["One-Time", "Full-time", "Remote", "On-call", "Temporary"].map((type) => (
                      <label key={type} className="flex items-center gap-3 p-2">
                        <input
                          type="checkbox"
                          checked={selectedTypes.includes(type)}
                          onChange={() => handleTypeChange(type)}
                        />
                        <span>{type}</span>
                      </label>
                    ))}
                  </div>

                  <div>
                    <h4 className="font-medium text-slate-700 mb-3">Location</h4>
                    <input
                      type="text"
                      value={locationFilter}
                      onChange={(e) => setLocationFilter(e.target.value)}
                      placeholder="e.g. Kibera, Mathare"
                      className="w-full px-4 py-2 border rounded-lg"
                    />
                  </div>

                  <div>
                    <h4 className="font-medium text-slate-700 mb-3">Pay Range (KES/h)</h4>
                    {["Below 500", "500 - 1000", "Above 1000"].map((range) => (
                      <label key={range} className="flex items-center gap-3 p-2">
                        <input
                          type="checkbox"
                          checked={payRange.includes(range)}
                          onChange={() => handlePayRangeChange(range)}
                        />
                        <span>{range}</span>
                      </label>
                    ))}
                  </div>
                </div>
              </div>
            </aside>

            <main className="lg:col-span-3">
              <div className="flex justify-between mb-6">
                <p className="text-slate-600">Showing filtered results</p>
                <div className="flex items-center gap-2">
                  <span className="text-slate-600 text-sm">Sort by:</span>
                  <select value={sortBy} onChange={(e) => setSortBy(e.target.value)} className="border rounded px-2 py-1">
                    <option>Most Recent</option>
                    <option>Highest Pay</option>
                  </select>
                </div>
              </div>

              <div className="grid gap-6">
                {filteredJobs.length === 0 ? (
                  <p className="text-slate-500 text-center">No jobs found.</p>
                ) : (
                  filteredJobs.map((job) => (
                    <div key={job.jobId} className="bg-white border rounded-xl p-6 shadow-sm">
                      <div className="flex flex-col sm:flex-row justify-between items-center">
                        <div>
                          <h2 className="text-xl font-semibold mb-1">{job.title}</h2>
                          <p className="text-sm mb-1">{job.location}</p>
                          <p>{job.description}</p>
                          <p className="text-indigo-600 text-sm mt-2">{job.category}</p>
                        </div>
                        <div className="flex flex-col items-end gap-2">
                          <button onClick={() => applyToJob(job.jobId)} className="mt-4 px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700">
                            Apply
                          </button>
                          <button className="text-sm text-slate-500 hover:text-slate-700">
                            Bookmark
                          </button>
                        </div>
                      </div>
                    </div>
                  ))
                )}
              </div>
            </main>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Jobs;
