import React, { useEffect, useState } from "react";
import axios from "axios";
import "../styles/style.css";

const Employerdash = () => {
  const [profile, setProfile] = useState(null);
  const [stats, setStats] = useState({ jobsPosted: 0, totalApplicants: 0 });

  useEffect(() => {
    const token = localStorage.getItem("token");

    const fetchProfile = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/users/me", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setProfile(res.data);
      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };

    const fetchStats = async () => {
      try {
        const res = await axios.get("http://localhost:5000/api/employerStats/employer", {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        });
        setStats(res.data);
      } catch (err) {
        console.error("Error fetching stats:", err);
      }
    };

    fetchProfile();
    fetchStats();
  }, []);

  return (
    <div id="webcrumbs">
      <div className="w-full max-w-6xl mx-auto p-6 bg-gradient-to-br from-slate-50 to-blue-50 min-h-screen">
        <div className="text-center mb-6">
          <h1 className="text-3xl font-bold text-slate-800">Employer Dashboard</h1>
          {profile && (
            <p className="text-slate-600 mt-2">
              Welcome back, {profile.firstName} {profile.lastName}
            </p>
          )}
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div className="bg-white p-6 rounded-2xl shadow border border-slate-200">
            <h2 className="text-xl font-semibold text-slate-700 mb-2">Jobs Posted</h2>
            <p className="text-3xl font-bold text-primary-500">{stats.jobsPosted}</p>
          </div>

          <div className="bg-white p-6 rounded-2xl shadow border border-slate-200">
            <h2 className="text-xl font-semibold text-slate-700 mb-2">Total Applicants</h2>
            <p className="text-3xl font-bold text-primary-500">{stats.totalApplicants}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Employerdash;
