import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "../styles/rate.css";

const RateApplicant = () => {
  const { applicantId } = useParams();
  const [applicant, setApplicant] = useState(null);
  const [feedback, setFeedback] = useState("");
  const [score, setScore] = useState(0);
  const [hoveredStar, setHoveredStar] = useState(0);
  const [recommended, setRecommended] = useState(null);

  useEffect(() => {
    const fetchApplicant = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/applicants/${applicant.applicantId}`);
        setApplicant(res.data);
      } catch (err) {
        console.error("Error fetching applicant:", err);
      }
    };
    fetchApplicant();
  }, [applicantId]);

  const handleSubmit = async () => {
    if (score === 0 || recommended === null) {
      alert("Please complete score and recommendation.");
      return;
    }

    try {
      await axios.post("/api/ratings", {
        applicantId,
        feedback: `${feedback}\n\nScore: ${score}/5\nRecommendation: ${recommended ? "Yes" : "No"}`
      });

      alert("Rating submitted successfully");
      setFeedback("");
      setScore(0);
      setRecommended(null);
    } catch (err) {
      console.error("Error submitting rating:", err);
      alert(err.response?.data?.error || "Failed to submit rating.");
    }
  };

  const RecommendationButtons = () => (
    <div className="flex items-center space-x-4">
      <button
        type="button"
        className={`flex items-center space-x-2 px-4 py-2 rounded-lg border-2 ${
          recommended === true
            ? 'bg-green-50 border-green-500 text-green-700'
            : 'bg-white border-gray-300 text-gray-600 hover:border-green-300 hover:bg-green-50'
        }`}
        onClick={() => setRecommended(true)}
      >
        <span className="text-2xl">👍</span>
        <span className="font-medium">Recommend</span>
      </button>
      <button
        type="button"
        className={`flex items-center space-x-2 px-4 py-2 rounded-lg border-2 ${
          recommended === false
            ? 'bg-red-50 border-red-500 text-red-700'
            : 'bg-white border-gray-300 text-gray-600 hover:border-red-300 hover:bg-red-50'
        }`}
        onClick={() => setRecommended(false)}
      >
        <span className="text-2xl">👎</span>
        <span className="font-medium">Not Recommend</span>
      </button>
    </div>
  );

  const StarRating = () => {
    const stars = [];
    for (let i = 1; i <= 5; i++) {
      stars.push(
        <button
          key={i}
          type="button"
          className={`text-3xl ${i <= (hoveredStar || score) ? 'text-yellow-400' : 'text-gray-300'}`}
          onClick={() => setScore(i)}
          onMouseEnter={() => setHoveredStar(i)}
          onMouseLeave={() => setHoveredStar(0)}
        >
          ★
        </button>
      );
    }
    return (
      <div className="flex items-center space-x-1">
        {stars}
        <span className="ml-3 text-sm text-gray-600">{score > 0 ? `${score}/5` : "Select a score"}</span>
      </div>
    );
  };

  if (!applicant) return <div className="text-center py-10 text-gray-600">Loading applicant data...</div>;

  return (
    <div className="min-h-screen bg-gray-50 py-8">
      <div className="max-w-2xl mx-auto px-4">
        <div className="bg-white rounded-lg shadow-md p-6">
          <h1 className="text-2xl font-bold text-gray-900 mb-6">Rate Applicant</h1>

          <div className="flex items-center space-x-4 mb-6">
            <div className="w-16 h-16 bg-indigo-100 rounded-full flex items-center justify-center">
              <span className="text-xl font-semibold text-indigo-600">
                {applicant.firstName[0]}{applicant.lastName[0]}
              </span>
            </div>
            <div>
              <h2 className="text-xl font-semibold text-gray-900">{applicant.firstName} {applicant.lastName}</h2>
              <p className="text-gray-600">{applicant.email}</p>
            </div>
          </div>

          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-3">Recommendation</label>
              <RecommendationButtons />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-3">Score</label>
              <StarRating />
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Feedback</label>
              <textarea
                rows={4}
                value={feedback}
                onChange={(e) => setFeedback(e.target.value)}
                placeholder="Write your feedback here..."
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-indigo-500 resize-none"
              />
            </div>

            <div className="text-right">
              <button
                onClick={handleSubmit}
                className="px-6 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700"
              >
                Submit Rating
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default RateApplicant;
