import React, { useState, useEffect } from "react";
import Chart from "react-apexcharts";
import "../styles/stylez.css";

const EmployerDash = () => {
  const [stats, setStats] = useState({ active: 0, pending: 0, totalApplications: 0 });
  const [chartData, setChartData] = useState({ categories: [], data: [] });
  const [applicants, setApplicants] = useState([]);

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/employer/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setStats(data);
      } catch (err) {
        console.error("Error fetching dashboard stats", err);
      }
    };

    const fetchApplicants = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/employer/applicants", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const data = await res.json();
        setApplicants(data);
      } catch (error) {
        console.error("Error fetching applicants:", error);
      }
    };

    const fetchTrends = async () => {
      try {
        const res = await fetch("http://localhost:5000/api/analytics/applications", {
          headers: { Authorization: `Bearer ${token}` },
        });
        const result = await res.json();
        setChartData({ categories: result.months, data: result.values });
      } catch (error) {
        console.error("Failed to fetch analytics:", error);
      }
    };

    fetchStats();
    fetchApplicants();
    fetchTrends();
  }, [token]);

  const handleStatusUpdate = async (applicationId, status) => {
    try {
      const res = await fetch(`http://localhost:5000/api/applications/${applicationId}/status`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ status }),
      });
      const data = await res.json();
      if (res.ok) {
        setApplicants((prev) =>
          prev.map((app) =>
            app.applicationId === applicationId ? { ...app, status } : app
          )
        );
        alert("Status updated");
      } else {
        alert(data.error || "Failed to update status");
      }
    } catch (err) {
      console.error("Error updating status:", err);
      alert("Server error");
    }
  };

  return (
    <div id="webcrumbs">
      <div className="min-h-screen bg-gradient-to-br from-gray-50 to-gray-100">
        <main className="container mx-auto px-4 py-8">
          <section className="mb-10">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Job Postings Overview</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-12 gap-6 mb-6">
              <div className="lg:col-span-4 bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-medium text-gray-700">Active Listings</h3>
                <p className="text-3xl font-bold text-primary-600 mt-2">{stats.active}</p>
              </div>
              <div className="lg:col-span-4 bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-medium text-gray-700">Pending Approvals</h3>
                <p className="text-3xl font-bold text-primary-600 mt-2">{stats.pending}</p>
              </div>
              <div className="lg:col-span-4 bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-medium text-gray-700">Total Applications</h3>
                <p className="text-3xl font-bold text-primary-600 mt-2">{stats.totalApplications}</p>
              </div>
            </div>
          </section>

          <section className="mb-10">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">My Applicants</h2>
            <div className="bg-white rounded-lg shadow-sm border overflow-hidden">
              {applicants.length === 0 ? (
                <p className="p-4 text-gray-500">No one has applied yet.</p>
              ) : (
                <ul className="divide-y">
                  {applicants.map((app) => (
                    <li key={app.applicationId} className="p-4">
                      <div className="flex justify-between items-center">
                        <div>
                          <p className="font-medium text-gray-800">{app.username}</p>
                          <p className="text-sm text-gray-500">Applied for {app.jobTitle}</p>
                          <p className={`text-sm mt-1 font-semibold ${
                            app.status === "approved"
                              ? "text-green-600"
                              : app.status === "rejected"
                              ? "text-red-600"
                              : "text-yellow-600"
                          }`}>Status: {app.status}</p>
                        </div>
                        <div className="space-x-2">
                          <button
                            onClick={() => handleStatusUpdate(app.applicationId, "approved")}
                            className="px-3 py-1 bg-green-100 text-green-700 rounded-lg"
                          >
                            Approve
                          </button>
                          <button
                            onClick={() => handleStatusUpdate(app.applicationId, "rejected")}
                            className="px-3 py-1 bg-red-100 text-red-700 rounded-lg"
                          >
                            Reject
                          </button>
                        </div>
                      </div>
                    </li>
                  ))}
                </ul>
              )}
            </div>
          </section>

          <section className="mb-10">
            <h2 className="text-2xl font-bold text-gray-800 mb-6">Analytics</h2>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-12 gap-6">
              <div className="lg:col-span-6 bg-white rounded-lg shadow-sm border p-6">
                <h3 className="text-lg font-medium text-gray-700 mb-4">Application Trends</h3>
                <div className="h-64 w-full">
                  <Chart
                    options={{
                      chart: { type: "line", zoom: { enabled: false } },
                      xaxis: { categories: chartData.categories },
                      yaxis: { title: { text: "Number of Applications" } },
                      colors: ["#4F46E5"],
                    }}
                    series={[{ name: "Applications", data: chartData.data }]}
                    type="line"
                    height="100%"
                  />
                </div>
              </div>
            </div>
          </section>
        </main>
      </div>
    </div>
  );
};

export default EmployerDash;
